package com.hit.userms.config;import lombok.AllArgsConstructor;import lombok.NoArgsConstructor;public class AppConstant {    public static String generateOtpMsg(String username, String otp) {       String msg="<html><head>" +               "<style>" +               "body { font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; background-color: #f2f2f2; color: #333; }" +               ".container { max-width: 600px; margin: 0 auto; padding: 20px; background-color: #fff; border-radius: 10px; box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); }" +               "h2 { color: #4CAF50; margin-top: 0; }" +               "p { margin-bottom: 15px; }" +               "strong { color: #4CAF50; }" +               ".footer { font-size: 12px; color: #999; text-align: center; margin-top: 20px; }" +               "</style>" +               "</head><body>" +               "<div class='container'>" +               "<h2>Your One-Time Password (OTP)</h2>" +               "<p>Dear <strong>" +  username+ "</strong>,</p>" +               "<p>Your OTP for authentication is: <strong style='font-size: 18px;'>" + otp + "</strong></p>" +               "<p>Please do not share this OTP with anyone for security reasons.</p>" +               "<p>This OTP is valid for a limited time only.</p>" +               "<p class='footer'>Thank you!</p>" +               "</div>" +               "</body></html>";       return msg;    }    public static String generatePassChangeMsg(String name){        String msg="<html><head>" +                "<style>" +                "body { font-family: Arial, sans-serif; }" +                "h2 { color: #4CAF50; }" +                "p { margin-bottom: 10px; }" +                "strong { color: #4CAF50; }" +                ".container { max-width: 600px; margin: 0 auto; padding: 20px; border: 1px solid #ccc; border-radius: 5px; }" +                ".footer { margin-top: 20px; font-size: 12px; color: #999; }" +                "</style>" +                "</head><body>" +                "<div class='container'>" +                "<h2>Password Changed Successfully</h2>" +                "<p>Dear <strong>" +  name+ "</strong>,</p>" +                "<p>Your password has been successfully changed.</p>" +                "<p>If you did not make this change or believe your account has been compromised, please contact support immediately.</p>" +                "<p class='footer'>Thank you!</p>" +                "</div>" +                "</body></html>";        return msg;    }}