package com.hit.userms.dto;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.annotation.JsonProperty;import com.hit.userms.enums.Gender;import jakarta.validation.constraints.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@NoArgsConstructor@AllArgsConstructor@Datapublic class UserDTO {    @NotEmpty(message = "Name must not Empty")    private String fullName;    private Integer userId;    @NotEmpty(message = "can not be empty")    @Pattern(regexp = "\\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,}\\b", message = "Email is not valid!!")    private String email;    private String mobileNo;    private String profilePhoto;    @Size(min = 6,max = 10,message = "Password length must be 6 to 10")    private String password;    @JsonIgnore    public String getPassword() {        return password;    }    @JsonProperty    public void setPassword(String password) {        this.password = password;    }    private Gender gender;}