package com.hit.userms.model;import com.hit.userms.enums.Gender;import com.hit.userms.enums.Role;import jakarta.persistence.*;import jakarta.validation.constraints.Email;import jakarta.validation.constraints.NotEmpty;import jakarta.validation.constraints.Size;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;import java.util.List;@Entity@NoArgsConstructor@AllArgsConstructor@Datapublic class UserEO {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Integer userId;    private String fullName;    @Column(unique = true)    private String email;    @Column(unique = true)    private String mobileNo;    private String profilePhoto;    private String password;    @Enumerated(EnumType.STRING)    private Gender gender;    private String Otp;    private LocalDateTime otpExpirationTime;    @OneToMany(mappedBy = "userEO")    private List<AddressEO> addressEOS;    @Enumerated(EnumType.STRING)    private Role role;}