package com.hit.userms.repo;import com.hit.userms.model.RefreshToken;import com.hit.userms.model.UserEO;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;public interface RefreshTokenRepo extends JpaRepository<RefreshToken,Integer > {    RefreshToken findByUserEO(UserEO userEO);    @Query("SELECT rt FROM RefreshToken rt WHERE rt.rToken = ?1")    RefreshToken findByRToken(String token);}