package com.hit.userms.security;import com.hit.userms.model.UserEO;import com.hit.userms.repo.UserRepo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.User;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class CustomUserDetailsService implements UserDetailsService {    @Autowired    private UserRepo userRepo;    @Override    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        UserEO userEO=null;        if(username.contains("@")){            userEO=this.userRepo.findByEmail(username);        }        else{            userEO=this.userRepo.findByMobileNo(username);        }        if(userEO==null) {            throw  new UsernameNotFoundException("User is not present");        }        return CustomUser.builder()                .userEO(userEO)                .build();    }}